CREATE TABLE car_master (
    ID INT PRIMARY KEY AUTO_INCREMENT,
    ChassisNo VARCHAR(50) UNIQUE,
    Model VARCHAR(50),
    ProductLine VARCHAR(50),
    Colour VARCHAR(50),
    PhysicalStatus VARCHAR(50),
    EngineNo VARCHAR(50),
    EmissionNorm VARCHAR(10),
    ManufacturingDate DATE,
    TMInvoiceDate DATE,
    CommercialInvoiceNo VARCHAR(50),
    HSNCode VARCHAR(20),
    MakersName VARCHAR(50),
    NoOfCylinders INT,
    SeatingCapacity INT,
    CatalyticConverter BOOLEAN,
    UnladenWeight INT,
    FrontAxleWeight INT,
    RearAxleWeight INT,
    AnyOtherAxleWeight INT,
    TandemAxleWeight INT,
    GrossWeight INT,
    TypeOfBody VARCHAR(50)
);

